import {ADD_ARTICLE, DELETE_ITEM} from "../constants/action-types";

export function addArticle(payload) {
    return { type: ADD_ARTICLE, payload }
  };

export function delItem(id) {
  return {type : DELETE_ITEM, id:id,}
}
